import Foundation

/// This calendar variant respects the original Article III: a year starts the day of the equinox, in the Paris meridian time
struct DelambreVariant: RepublicanCalendarVariant {
    var maxSafeDate: Date {
        Date(timeIntervalSince1970: 32503593600) // 2999-12-31
    }

    func lookupConversion(year: Int) -> Int {
        // year starts at 1 but array starts at 0
        if conversionTable.indices.contains(year - 1) {
            return Int(conversionTable[year - 1])
        } else {
            return 22 // sensible default, although past our maxSafeRange so shouldn't be possible to get here under normal circumstable
        }
    }

    func isYearSextil(_ year: Int) -> Bool {
        let startDay = lookupConversion(year: year)
        let endDay = lookupConversion(year: year + 1)
        let dayDiff = endDay - startDay
        
        let gregorianIsBissextil = gregorian.isYearSextil(year + 1792)
        let yearDiff = gregorianIsBissextil ? 1 : 0
        
        let diff = dayDiff + yearDiff
        assert(diff == 0 || diff == 1)
        return diff == 1
    }
    
    func convertToRepublican(gregorian date: Date, in gregorianCalendar: Calendar) -> (dayOfYear: Int, year: Int) {
        let gYear = gregorianCalendar.dateComponents([.year], from: date).year!
        
        var year: Int = gYear - 1792
        var dayOfYear: Int = gregorianCalendar.ordinality(of: .day, in: .year, for: date)!
        
        increment(day: &dayOfYear, by: 122 - lookupConversion(year: year), year: &year)
        
        return (dayOfYear, year)
    }
    
    func convertToGregorian(rDayInYear: Int, rYear: Int, in gregorianCalendar: Calendar) -> (dayOfYear: Int, year: Int) {
        var gYear = rYear + 1792
        var gDayOfYear = rDayInYear
        gregorian.increment(day: &gDayOfYear, by: -124 + lookupConversion(year: rYear), year: &gYear)
        
        return (gDayOfYear, gYear)
    }
}

extension RepublicanCalendarVariant where Self == DelambreVariant {
    static var delambre: Self { DelambreVariant() }
}

